-- ----------------------------
-- Table structure for comment
-- ----------------------------
DROP TABLE IF EXISTS `comment`;
CREATE TABLE `comment` (
  `comment_id` varchar(200) NOT NULL,
  `reviewer_id` varchar(200) NOT NULL,
  `content` varchar(255) NOT NULL,
  `comment_time` datetime NOT NULL ON UPDATE CURRENT_TIMESTAMP,
  KEY `comment_id` (`comment_id`),
  KEY `reviewer_id` (`reviewer_id`),
  CONSTRAINT `comment_id` FOREIGN KEY (`comment_id`) REFERENCES `project` (`pro_id`),
  CONSTRAINT `reviewer_id` FOREIGN KEY (`reviewer_id`) REFERENCES `students` (`stu_num`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

