  `pro_introduce` text NOT NULL,
  `pro_pic` text,
  `pro_good` int(10) unsigned zerofill NOT NULL,
  PRIMARY KEY (`pro_id`),
  KEY `_id` (`stu_id`),
  CONSTRAINT `_id` FOREIGN KEY (`stu_id`) REFERENCES `students` (`stu_num`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

